mapper.select:
SELECT DISTINCT row_number() over (ORDER BY RI_NUM DESC) rnum, MEM_ID, RI_MEM_ID, MEM_NICK, RI_GENDER, RI_LOAN_BIG, RI_LOAN_SMALL, RI_REGION1, RI_REGION2, RI_REGION3, RI_DATE_START, RI_DATE_END, NVL(TRUNC(MONTHS_BETWEEN(TO_CHAR(SYSDATE,'YYYYMMDD'), TO_CHAR(RI_BIRTH, 'YYYYMMDD'))/12), 0) AS RI_BIRTH
FROM MEMBER M,
(SELECT DISTINCT * FROM ROOMMATE_PROFILE WHERE RI_REGION1 LIKE %#{RI_REGION,jdbcType=VARCHAR}% OR RI_REGION2 LIKE %#{RI_REGION,jdbcType=VARCHAR}% OR RI_REGION3 LIKE %#{RI_REGION,jdbcType=VARCHAR}%) R,
ROOMMATE_PROFILE R
WHERE M.MEM_ID=R.RI_MEM_ID and RI_EXPOSE=1 and MEM_ID NOT IN ( SELECT DISTINCT IGNORE_D_MEM FROM IGNORE WHERE IGNORE_MEM = #{MEM_ID,jdbcType=VARCHAR} )
AND MEM_NICK LIKE '%' || #{MEM_NICK,jdbcType=VARCHAR} || '%'
AND RI_GENDER = #{RI_GENDER,jdbcType=VARCHAR}
AND RI_BIRTH > #{minyear,jdbcType=DATE}
AND RI_BIRTH < #{maxyear,jdbcType=DATE}
AND RI_LOAN_BIG > #{mindeposit,jdbcType=INTEGER}
AND RI_LOAN_BIG < #{maxdeposit}
AND RI_LOAN_SMALL > #{minrent,jdbcType=INTEGER}
AND RI_LOAN_SMALL < #{maxrent}
AND RI_DATE_START > #{mindate,jdbcType=DATE}
AND RI_DATE_END < #{maxdate,jdbcType=DATE}

	
	SELECT DISTINCT 
	COUNT(*)
FROM
	MEMBER M,


	
	(SELECT DISTINCT * FROM ROOMMATE_PROFILE 
	WHERE
		RI_REGION1 LIKE %#{RI_REGION,jdbcType=VARCHAR}%
		OR RI_REGION2 LIKE %#{RI_REGION,jdbcType=VARCHAR}%
		OR RI_REGION3 LIKE %#{RI_REGION,jdbcType=VARCHAR}%) R,
		


	
	ROOMMATE_PROFILE R
	


WHERE
	M.MEM_ID=R.RI_MEM_ID
	and RI_EXPOSE=1
	and MEM_ID NOT IN (
		SELECT DISTINCT 
			IGNORE_D_MEM
		FROM IGNORE
		WHERE IGNORE_MEM = #{MEM_ID ,jdbcType=VARCHAR}
		)
		

	
	AND MEM_NICK LIKE '%' || #{MEM_NICK,jdbcType=VARCHAR} || '%'
	

	

	
	AND RI_GENDER = #{RI_GENDER,jdbcType=VARCHAR}
	


	
	 AND RI_BIRTH > #{minyear,jdbcType=DATE} 
	


	
	 AND RI_BIRTH < #{maxyear,jdbcType=DATE} 
	



	
	AND RI_LOAN_BIG > #{mindeposit,jdbcType=INTEGER} 



	
	AND RI_LOAN_BIG < #{maxdeposit} 




	
	AND RI_LOAN_SMALL > #{minrent,jdbcType=INTEGER} 
	



	
	AND RI_LOAN_SMALL < #{maxrent} 
	



	
	AND RI_DATE_START > #{mindate,jdbcType=DATE} 
	


	
	AND RI_DATE_END < #{maxdate,jdbcType=DATE}

mapper.insert:
	
	INSERT INTO FAV_MATE(
	FAV_MATE_NUM,
	MEM_ID,
	RI_MEM_ID
)
VALUES(
	FAV_MATE_NUM_SEQ.NEXTVAL,
	#{MEM_ID,jdbcType=VARCHAR},
	#{RI_MEM_ID,jdbcType=VARCHAR}
)

mapper.delete:
	
	DELETE FROM FAV_MATE
WHERE
	MEM_ID = #{MEM_ID,jdbcType=VARCHAR}
	AND RI_MEM_ID = #{RI_MEM_ID,jdbcType=VARCHAR}
