mapper.select:
 

SELECT
	ROW_NUMBER() OVER (ORDER BY S.ROOM_NUM DESC) ROW_NUM,
	S.ROOM_NUM,
	S.ROOM_TYPE,
	S.TRADE_TYPE,
	S.MONTHLY_PAYMENT,
	S.MONTHLY_DEPOSIT,
	S.JEONSE,
	S.THUMBNAIL,
	S.ADDRESS1,
	S.REAL_SIZE,
	S.ROOM_FLOOR,
	S.UTILITY_PRICE,
	S.DESC_TITLE
FROM
	(
		SELECT
			ROW_NUMBER() OVER (ORDER BY R1.ROOM_NUM DESC) RNUM,
			R1.ROOM_NUM,
			R1.ROOM_TYPE,
			R1.TRADE_TYPE,
			R1.MONTHLY_PAYMENT,
			R1.MONTHLY_DEPOSIT,
			R1.JEONSE,
			R1.THUMBNAIL,
			R1.ADDRESS1,
			R2.REAL_SIZE,
			R2.ROOM_FLOOR,
			R2.UTILITY_PRICE,
			R2.DESC_TITLE
		FROM
			ROOM_INFO1 R1,
			ROOM_INFO2 R2,
			ROOM_BOARD RB
		WHERE
			R1.ROOM_NUM=R2.room_num and
			R1.ROOM_NUM=RB.room_num and
			RB.DEL_GB='N' and
			RB.TRADE_STATUS='광고중'






	 AND R1.ROOM_NUM IN(SELECT ROOM_NUM FROM ROOM_INFO1 R1 WHERE ADDRESS1 LIKE '%'||#{address}||'%')




	
		AND R1.ROOM_TYPE in
	
		
	
			#{item}
	
		


	

	AND R1.MONTHLY_PAYMENT=#{MONTHLY_PAYMENT}





	AND R1.MONTHLY_DEPOSIT=#{MONTHLY_DEPOSIT}





	AND R1.JEONSE=#{JEONSE}




	AND R1.MONTHLY_PAYMENT > #{minPayment}




	AND R1.MONTHLY_PAYMENT < #{maxPayment}




	AND R1.MONTHLY_DEPOSIT>#{minMdeposit}




	AND R1.MONTHLY_DEPOSIT<#{maxMdeposit}




	AND R1.JEONSE>#{minJeonse}




	AND R1.JEONSE<#{maxJeonse}

	

	
		AND R1.TRADE_TYPE in
	
		
	
			#{item}
	
		


	
		
			AND R2.UTILITY_PRICE>#{minUtility}
		
	
	
		
			AND R2.UTILITY_PRICE>#{maxUtility}
		
	



	AND R2.REAL_SIZE=#{REAL_SIZE}




		AND R2.ROOM_FLOOR in
	
		
	
			#{item}
	
		



		AND R2.ROOM_FLOOR >#{ROOM_FLOOR7}





			AND R2.STRUCTURES in
	
		
	
			#{item}
	
		



	AND R2.PARKING=#{PARKING}




	AND R2.PET=#{PET}




	AND R2.ELEVATOR=#{ELEVATOR}




	AND R2.BALCONY=#{BALCONY}




	AND R2.BUILT_IN=#{BUILT_IN}




	AND R2.OPTIONS=#{OPTIONS}




	AND R2.LOAN_ACCESS=#{LOAN_ACCESS}



	) S ORDER BY ROW_NUM DESC

) AA ) AAA WHERE AAA.ROW_NUM BETWEEN 1 AND 15

SELECT
	count (*)
FROM
	(
		SELECT
			ROW_NUMBER() OVER (ORDER BY R1.ROOM_NUM DESC) RNUM,
